html:
{% set cadd = frappe.db.get_value('Address',doc.company_address)%}
{%if cadd%}
{% set ig = frappe.get_doc('Address',doc.company_address)%}
{%endif%}
{% set cs = frappe.db.get_value('Address',doc.customer_address,"gst_state_number")%}




    <table style="width:100%;border:1px solid black;">
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>


    </tr>


</table>
  <div class="table1">
          <table  style="width:100%;border:1px solid black;">
              <tr>
                  <td>Invoice</td>
                  <td>{{doc.name}}</td>
                  <td> Invoice Date</td>
                  <td>{{doc.get_formatted("posting_date")}}</td>
                  <td>Time</td>
                  <td>{{doc.get_formatted("posting_time")}}</td>
              </tr>
              <tr>
                  <td>Name</td>
                  <td>{{doc.customer}}:{{doc.customer_name}}</td>
                  <td>Vehicle no.</td>
                  <td>{{doc.vehicle_no}}</td>
            </tr>
              <tr>
                  <td>Address</td>
                  <td>{{doc.territory}}</td>
                  <td>Party GST No.</td>
                  <td>{{doc.customer_gstin}}</td>
            </tr>
          </table>
        </div>

<div class="table2">
    <table style="margin-top:2px;width:100%;border-bottom:none;">
        <tr>
            <th style="width:5%; color:black">Sr No.</th>
            <th style="width:5%; color:black">Item Name</th>
            <th style="width:5%; color:black">HSN/SAC</th>
            <th style="width:5%; color:black">Crate/Box</th>
            <th style="width:5%; color:black">Qty</th>
            <th style="width:5%; color:black">Ltr/Kg</th>
            <th style="width:5%; color:black">Rate</th>
            <th style="width:5%; color:black">Amount</th>
            <th style="width:5%; color:black">Less Disc</th>
            <th style="width:5%; color:black">Taxable Amt</th>

            {% if ig.gst_state_number == cs or not cs %}
            <th style="width:5%; color:black">CGST Amount</th>
            <th style="width:5%; color:black">Sgst Amount</th>
            {% endif %}

            {% if ig.gst_state_number != cs  and cs%}
            <th style="width:5%; color:black">IGST Amount</th>
            {% endif %}
            <th style="width:5%; color:black">Total Amount</th>
        </tr>
        {%for i in doc.items%}
        <tr >
            <td>{{loop.index}}</td>
            <td>{{i.item_name}}</td>
            <td>{{i.gst_hsn_code}}</td>

            {%if i.uom=="Crate"%}
            <td>{{i.qty}}</td>
            <td>{{i.stock_qty}}</td>
            {%elif i.uom=="Box"%}
             <td>{{i.qty}}</td>
            <td>{{i.stock_qty}}</td>
            {%else%}
             <td>{{0}}</td>
            <td>{{i.qty}}</td>
             {% endif %}
            <td>{{i.total_weight}}</td>
            <td>{{i.rate}}</td>
            <td>{{"%.2f"| format(i.net_amount)}}</td>
                <td>{{i.discount_amount}}</td>
            <td>{{"%.2f"| format(i.net_amount)}}</td>
            {%set item%}{%endset%}
            {% if i.item_tax_template %}
                {%set item = frappe.get_doc('Item Tax Template',i.item_tax_template)%}
              {% endif %}
             {% if ig.gst_state_number == cs or not cs%}

            <td>{%if item%}{{"%.2f"| format(((item.tax_rate * i.net_amount)/100))}}{%else%}0.0{%endif%}</td>
            <td>{%if item%}{{"%.2f"| format((item.tax_rate * i.net_amount)/100)}}{%else%}0.0{%endif%}</td>
            <td>{%if item%}{{"%.2f"| format(((item.tax_rate * i.net_amount)/100) + i.net_amount)}}{%else%} {{"%.2f"| format(i.net_amount)}}{%endif%}</td>
             {% endif %}

            {% if ig.gst_state_number != cs and cs %}
             <td>{%if item%}{{"%.2f"| format((item.tax_rate * 2*i.net_amount)/100)}}{%else%}0.0{%endif%}</td>
             <td>{%if item%}{{"%.2f"| format(((item.tax_rate * i.net_amount)/100) + i.net_amount)}}{%else%}{{"%.2f"| format(i.net_amount)}}{%endif%}</td>
             {% endif %}



        </tr>
        {% endfor %}
    </table>
</div>

<div  class="table3" style="width:100%;" >
    <table style="width:100%;">
        <tr style="width:150%;">
            <td style="width:600px;">Amt In word Rs:</td>
            <td style="width:1200px;" >{{ frappe.utils.money_in_words(doc.grand_total) }}</td>
             <td></td>
             <td></td>
            <td style=" border: 1px solid black;width:700px;">Total Amount Before Tax</td>
            <td style=" border: 1px solid black;" class ="text-right">{{doc.net_total}}</td>
        </tr>

        <tr>
            <td>DueBalance:</td>
            {%set bal= doc.party_balance%}
            <td>{%if bal%}{{bal}}{%endif%}</td>
            <td></td>
            <td></td>
             {% set vars = {'jcase': 0} %}
             {%- for item in doc.items -%}
             {% if item.item_tax_template %}
            {%set k = frappe.get_doc('Item Tax Template',item.item_tax_template)%}
            {% if vars.update({'jcase': vars.jcase + (k.tax_rate *item.net_amount)/100}) %}{% endif %}
            {% endif %}
            {%- endfor -%}
             {% if ig.gst_state_number == cs or not cs %}
                <td style=" border: 1px solid black;width:20%;"> CGST Amount</td>
                <td style=" border: 1px solid black;">{{"%.2f"| format(vars.jcase/2)}} </td>
            {% endif %}
        </tr>
        <tr>
            <td>GST NO :</td>
            <td>{{doc.company_gstin}}</td>
            <td style="width:20%;">FSSAI LIC.NO.:</td>
            <td>{{ig.fssai_lic_no}}</td>

            {% set vars = {'jcase': 0} %}
             {%- for item in doc.items -%}
             {% if item.item_tax_template %}
            {%set k = frappe.get_doc('Item Tax Template',item.item_tax_template)%}
            {% if vars.update({'jcase': vars.jcase + (k.tax_rate *item.net_amount)/100}) %}{% endif %}
            {% endif %}
            {%- endfor -%}
             {% if ig.gst_state_number == cs or not cs %}
                <td style=" border: 1px solid black;"> SGST Amount</td>
                <td style=" border: 1px solid black;"> {{"%.2f"| format(vars.jcase/2)}}</td>
             {% endif %}

        </tr>
        <tr>

        </tr>
    </table>

    <table style="width:100%;">
  <tr>
   <td rowspan ="{{ rowspan }}" style ="width:57%;">
      <p>Gst Payable RS Charges:</p>
      <p>Certified that the particutars given above are true and correct.</p>
      <b>Bank Details : </b>
      {% set acc = frappe.db.get_value('Bank Account',{"is_default":1,"is_company_account":1,"company":doc.company},['name'])%}
         {%if acc%}
         {% set bank = frappe.get_doc('Bank Account',acc)%}
    <p>Bank Name:&nbsp;{{bank.bank}}
      <p>Account Name:  &nbsp; &nbsp; {{bank.account_name}}</p>
      <p>Account No.&nbsp;{{bank.bank_account_no}} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;IFSC Code:{{bank.branch_code}}</p>
      {% endif %}

   </td>

     {% set vars = {'jcase': 0} %}
     {%- for item in doc.items -%}
     {% if item.item_tax_template %}
    {%set k = frappe.get_doc('Item Tax Template',item.item_tax_template)%}
    {% if vars.update({'jcase': vars.jcase + (k.tax_rate * 2*item.net_amount)/100}) %}{% endif %}
    {% endif %}
    {%- endfor -%}
    {% if ig.gst_state_number != cs and cs %}
       <td style ="border: 1px solid black;">IGST Amount:</td>
       <td style ="border: 1px solid black;">{{"%.2f"| format(vars.jcase)}}</td>
    {% endif %}


  </tr>

  <tr>
     <td></td>
     <td style ="border: 1px solid black;">Round off Amount</td>
     <td>{{doc.rounded_total}}</td>

  </tr>
<tr>
   <td></td>

     <td style ="border: 1px solid black;"><b>Total Amount After Tax</b></td>
     <td>{{doc.grand_total}}</td> 
</tr>






 </table>

